{{ page.title | xml_escape }}
{{ site.description | xml_escape }}
{{ site.url }}{{ post.url }}

{{ site.time | date_to_rfc822 }}
{{ site.time | date_to_rfc822 }}
Jekyll v{{ jekyll.version }}

{% assign platform = 'twitter' %}

 {% for project in site.portfolio %}
    {% assign share_on = project.share | downcase %}

    {% if share_on contains '--twitter-title' %}
        {% assign post_title = project.title %}

    {% else %}

        {% assign format_parsed = site.twitter_format | replace: '@title', project.description | replace: '@url', '' | replace: '@tags', '' | size %}
        {% assign post_tags = '' %}
        {% assign space_left = site.twitter_max_length | minus: format_parsed.size %}

        {% if site.twitter_format contains '@url' %}
            {% assign space_left = space_left | minus: site.twitter_url_length %}
        {% endif %}

        {% for tag in project.tags %}
            {% capture new_tag %} #{{ tag }}{% endcapture %}
            {% assign new_space_left = space_left | minus: new_tag.size %}

            {% if new_space_left > 0 %}
                {% assign post_tags = post_tags | append: new_tag %}
                {% assign space_left = space_left | minus: new_tag.size %}
            {% endif %}
        {% endfor %}

        {% assign post_title = site.twitter_format | replace: '@title', project.description | replace: '@tags', post_tags | replace: '@url', project.url %}

    {% endif %}

    {% if share_on contains platform %}


        {{ post_title | xml_escape }}
        {{ project.description | xml_escape }}
        {{ project.date | date_to_rfc822 }}
        {{ project.url | prepend: site.url }}
        {{ project.url | prepend: site.url }}
        {% for tag in project.tags %}
        {{ tag | xml_escape }}
        {% endfor %}
        {% for cat in project.categories %}
        {{ cat | xml_escape }}
        {% endfor %}

    {% endif %}
{% endfor %}

{% for post in site.posts limit:10 %}
    {% assign share_on = post.share | downcase %}

     {% if share_on contains '--twitter-title' %}
        {% assign post_title = post.title %}

    {% else %}

        {% assign format_parsed = site.twitter_format | replace: '@title', post.description | replace: '@url', '' | replace: '@tags', '' | size %}
        {% assign post_tags = '' %}
        {% assign space_left = site.twitter_max_length | minus: format_parsed.size %}

        {% if site.twitter_format contains '@url' %}
            {% assign space_left = space_left | minus: site.twitter_url_length %}
        {% endif %}

        {% for tag in post.tags %}
            {% capture new_tag %} #{{ tag }}{% endcapture %}
            {% assign new_space_left = space_left | minus: new_tag.size %}

            {% if new_space_left > 0 %}
                {% assign post_tags = post_tags | append: new_tag %}
                {% assign space_left = space_left | minus: new_tag.size %}
            {% endif %}
        {% endfor %}

        {% assign post_title = site.twitter_format | replace: '@title', post.description | replace: '@tags', post_tags | replace: '@url', post.url %}

    {% endif %}

    {% if share_on contains platform %}


        {{ post_title | xml_escape }}
        {{ post.description | xml_escape }}
        {{ post.date | date_to_rfc822 }}
        {{ post.url | prepend: site.url }}
        {{ post.url | prepend: site.url }}
        {% for tag in post.tags %}
        {{ tag | xml_escape }}
        {% endfor %}
        {% for cat in post.categories %}
        {{ cat | xml_escape }}
        {% endfor %}

    {% endif %}
{% endfor %}
